package br.com.senaisp.aula02;

public class ExerciciosAula02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primeira expressão
		double dblConta = 2.00 / 5.00;
		System.out.println("Resultado da primeira expressão é :"+ dblConta);
		//Segunda expressão
		//Poderiamos criar uma outra variável como abaixo
		// double dblConta2 = 2.00/3.00 + 10;
		//Porém, vamos reaproveitar para fazer a conta da segunda expressão
		dblConta = 2.00/3.00 + 10;
		System.out.println("Resultado da segunda expressão é :" + dblConta);
		//terceira expressão
		dblConta = 1.00 / 5 * 3.00 / 4 + 5;
		System.out.println("Resultado da terceira expressão é :" + dblConta);
	}

}
